import React from "react";

import CustomButton from "../../components/button/button";
import InputForm from "../../components/inputform/inputform";
import "./signin.scss";
import { signInWithGoogle } from "../../firebase/firebase.utilities";

class SignIn extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
    };
  }

  onSubmitHandle = (event) => {
    event.preventDefault();

    this.setState({
      email: "",
      password: "",
    });
  };
  onChangeHadnle = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <div className="sign-in-container">
        <h1>I already have an account</h1>
        <span>Sign in with existing email and password</span>

        <form onSubmit={this.onSubmitHandle}>
          <InputForm
            name="email"
            type="email"
            label="email"
            value={this.state.email}
            onChange={this.onChangeHadnle}
            required
          />
          <InputForm
            name="password"
            label="password"
            type="password"
            value={this.state.password}
            onChange={this.onChangeHadnle}
            required
          />

          <CustomButton type="submit">SIGN IN</CustomButton>
          <CustomButton onClick={this.signInWithGoogle}>SIGN IN WITH GOOGLE</CustomButton>
        </form>
      </div>
    );
  }
}
export default SignIn;
