import React from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import { setToggleCart } from "../../redux/cart/cart-action";

import "./cart-item.scss";
import { ReactComponent as CartLogo } from "../../assets/cart.svg";
import { selectCartItemsCount } from "../../redux/cart/cart-selector";
import { CartIconContainer, CartIconCounter} from './cart-icon.styles'

const CartIcon = ({ setToggleCart, itemCount }) => (
  <CartIconContainer onClick={setToggleCart} >
    <CartLogo className="icon" />
    <CartIconCounter>{itemCount}</CartIconCounter>
  </CartIconContainer>
);

const mapStateToProps = createStructuredSelector({
  itemCount: selectCartItemsCount,
});

const mapDispatchToProps = (dispatch) => ({
  setToggleCart: () => dispatch(setToggleCart()),
});

export default connect(mapStateToProps, mapDispatchToProps)(CartIcon);
