import React from "react";

import "./inputform.scss";
import { GroupContainer, FormInputContainer, FormInputLabel} from  './inputform.styles'

const InputForm = ({ onChangeHandle, label, ...propsrops }) => (
  <GroupContainer>
    <FormInputContainer onChange={onChangeHandle} {...otherProps} />
    {label ? (
      <FormInputLabel
        className={
        props.value.length ? "shrink" : ""}
      >
        {label}
      </FormInputLabel>
    ) : null}
  </GroupContainer>
);
export default InputForm;
