import styled from "styled-components";
import CustomButton from "../button/button";

export const CollectionItemContainer = styled.div`
  width: 22vw;
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;

  &:hover {
    .image {
      opacity: 0.8;
    }
    button {
      opacity: 0.85;
      display: flex;
    }
  }
`;
export const CollectionPageTitle = styled.h2`
  font-size: 38px;
  margin: 0 auto 30px;
`;
export const CollectionPageFooter = styled.div`
  width: 100%;
  height: 5%;
  display: flex;
  justify-content: space-between;
  font-size: 18px;
`;
export const CollectionPageName = styled.span`
  width: 90%;
  margin-bottom: 15px;
`;
export const CollectionPagePrice = styled.span`
  width: 10%;
  text-align: right;
`;
export const AddButton = styled(CustomButton)`
  width: 80%;
  position: absolute;
  top: 255px;
  opacity: 0.7;
  display: none;
`;
export const BackgroundImage = styled.img`
  width: 100%;
  height: 95%;
  background-size: cover;
  background-position: center;
  margin-bottom: 5px;
  background-image: ${({ imageUrl }) => `url(${imageUrl})`};
`;
