import { userActions } from "./user-constants";

export const setCurrentUser = (user) => ({
  type: userActions.SET_CURRENT_USER,
  payload: user,
});

export const gooogleSignInStart = () => ({
  type: userActions.GOOGLE_SIGN_IN_START,
});
export const gooogleSignInSuccess = (user) => ({
  type: userActions.GOOGLE_SIGN_IN_SUCCESS,
  payload: user,
});
export const gooogleSignInFailure = (error) => ({
  type: userActions.GOOGLE_SIGN_IN_FAILURE,
  payload: error,
});
export const emailSignInStart = (emailAndPassword) => ({
  type: userActions.EMAIL_SIGN_IN_START,
  payload:emailAndPassword
});
export const emailSignInSuccess = (emailAndPassword) => ({
  type: userActions.EMAIL_SIGN_IN_SUCCESS,
  payload: emailAndPassword,
});
export const emailSignInFailure = (emailAndPassword) => ({
  type: userActions.EMAIL_SIGN_IN_FAILURE,
  payload: emailAndPassword,
});
