import { shopPageActions } from "./shopPage-constants";

const INITIAL_STATE = {
  collections: null
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case shopPageActions.UPDATE_COLLECTIONS:
      return {
        ...state,
        collections: action.paylod,
      };
    default:
      return state;
  }
};

export default shopReducer;
