import React from "react";

import "./inputform.scss";
import { FormInputContainer, GroupContainer } from "./inputform.styles";

const InputForm = ({ onChangeHandle, label, ...otherProps }) => (
  <GroupContainer>
    <InputFormComponent onChange={onChangeHandle} {...otherProps} />
    {label ? (
      <label
        className={`${
          otherProps.value.length ? "shrink" : ""
        } form-input-label`}
      >
        {label}
      </label>
    ) : null}
  </GroupContainer>
);
export default InputForm;
