import { cartToggleTypes, addItemToCart } from "./cart-constants";

const INITIAL_STATE = {
  hidden: true,
  cartitems: [],
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case cartToggleTypes.TOGGLE_CART:
      return {
        ...state,
        hidden: !state.hidden,
      };
    default:
      return state;
    case addItemToCart.ADD_ITEM_TO_CART:
      return {};
  }
};
export default cartReducer;
