import SHOP_DATA from './shop.data';
import { shopPageActions } from "./shopPage-constants";

const INITIAL_STATE = {
  collections: SHOP_DATA
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case  shopPageActions.UPDATE_COLLECTION:
      return{
        ...state,
        collections:action.paylaod
      }
    default:
      return state;
  }
};

export default shopReducer;
