import { cartToggleTypes } from './cart-constants';

const INITIAL_STATE = {
    hidden: true
}

const cartReducer = (state = INITIAL_STATE, action ) => {
    switch(action.type){
        case cartToggleTypes.TOGGLE_CART:
            return {
                ...state,
              hidden: !state.hidden
            }
            default:
                return state;
    }
}
export default cartReducer;
