import React from "react";

import './signin.scss';

class SignIn extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            email:'',
            password:''
        }
    }

    onSubmitHandle = event => {
        event.preventDefault();

        this.setState({
            email:'',
            password:''
        })
    } 
    onChangeHadnle = event => {
        const {value, name} = event.target;
        this.setState({
            [name]: value
        })
    }

    render(){

       
        return(
           <div>
                <h1>I already have an account</h1>
                <span>Sign in with existing email and password</span>

                <form onSubmit={this.onSubmitHandle}>
                    <input type='email' placeholder='Email' value={this.state.email} required/>
                    <input type='password' placeholder='password' value={this.state.password} required/>
                    <input type='submit' value='Submit' />
                </form>
           </div>

        )
    }
}
export default SignIn;
