import React from "react";

import CustomButton from "../../components/button/button";
import InputForm from "../../components/inputform/inputform";
import "./signin.scss";

import { signInWithGoogle, auth } from "../../firebase/firebase.utils";
import { SignInContainer,SignInTitle, SignInButtons} from './signin.styles'
class SignIn extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
    };
  }

  onSubmitHandle = async event => {
    const {email, password} = this.state;
    event.preventDefault();
    try{
      await auth.signInWithEmailAndPassword(email, password);

    this.setState({
      email: "",
      password: "",
    });
    }
    catch(error){
      console.log(error, 'error during signing in')
    }
  
  };
  onChangeHadnle = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { email, password } = this.state;
    return (
      <div className="sign-in-container">
        <h1>I already have an account</h1>
        <span>Sign in with existing email and password</span>

        <form onSubmit={this.onSubmitHandle}>
          <InputForm
            name="email"
            type="email"
            label="Email"
            value={email}
            onChange={this.onChangeHadnle}
            required
          />
          <InputForm
            name="password"
            label="password"
            type="Password"
            value={password}
            onChange={this.onChangeHadnle}
            required
          />
          <div className="buttons">
            <CustomButton type="submit"> Sign in </CustomButton>
            <CustomButton onClick={signInWithGoogle} isGoogleSignIn>
              Sign in with Google
            </CustomButton>
          </div>
        </form>
      </div>
    );
  }
}
export default SignIn;
