import React from "react";
import {connect} from 'react-redux'

import {removeItemsFromCart} from '../../redux/cart/cart-action'
import "./checkoutItem.scss";

const CheckoutItem = ({ cartItem: { name, quantity, price, imageUrl } }) => (
  <div className="checkout-item">
    <div className="image-container">
      <img src={imageUrl} alt="item" />
    </div>
    <span className="name">{name}</span>
    <span className="quantity">{quantity}</span>
    <span className="price">{price}z≈Ç</span>
    <div className="remove-button">&#10005;</div>
  </div>
);
const mapDispatchToProps = dispatch => ({
  clearItem: item => dispatch(removeItemsFromCart(item))
})
export default connect(mapDispatchToProps)(CheckoutItem);
